import React from 'react'
import { Link } from 'react-router-dom'
import {useState} from 'react';
import Validation from './LoginValidation';
import axios from 'axios'


function Login() {
    const [correo, setCorreo] = useState({});
    const [contra, setContra] = useState({});
    const [mensaje, setMensaje] = useState({});

    const handleSubmit = async (e) => {
        e.preventDefault();

        if(correo.correo_electronico === '' || contra.contrasenna === '' ){
            alert("Debe de rellenar todos los campos");
            return
        }

        try {
            const devolver = await axios.post('http://localhost:9000/api/login', {
              correo_electronico: correo,
              contrasenna: contra
            });
      
            setMensaje(devolver.data.mensaje);
          } catch (error) {
            setMensaje(error.devolver.data.error);
          }
    

  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
        <h2>Iniciar sesión</h2>
            <form action='' onSubmit={handleSubmit}>
                <div className='mb-3'>
                    <label htmlFor='correo_electronico'>Correo</label>
                    <input type='correo_electronico' placeholder='Enter correo_electronico' name='correo_electronico' onChange={(e) => setCorreo(e.target.value)} className='form-control rounded-0'></input>
                </div>
                <div className='mb-3'>
                    <label htmlFor='contrasenna'>Contraseña</label>
                    <input type='contrasenna' placeholder='Enter contrasenna' name='contrasenna' onChange={(e) => setContra(e.target.value)} className='form-control rounded-0'></input>
                </div>
                <button type='submit' className='btn btn-success w-100'>Iniciar Sesión</button>
                <p>Aceptar términos y condiciones</p>
                <Link to = '/signup' className='btn btn-default border w-100 text-decoration-none'>Crear cuenta</Link>
            </form>
        </div>   
    </div>
  )
}
}

export default Login
