import React from 'react'
import { Link } from 'react-router-dom'
import {useState} from 'react';
import Validation from './LoginValidation';
import axios from 'axios'


function Login() {
    const [values, setValues] = useState({
        correo_electronico: '',
        contrasenna: ''
    })
    const [errors, setErrors] = useState({})
    const handleInput = (event) => {
        setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
    }
    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(Validation(values));
        if(errors.correo_electronico === "" && errors.contrasenna === "") {
            axios.post('http://localhost:9000/login',values)
            .then(res => {
                if(res.data === "login_exitoso"){
                    navigate('/home');
                }else{
                    alert("Error");
                }

            })
            .catch(err => console.log(err));
        }
    }

  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
        <h2>Iniciar sesión</h2>
            <form action='' onSubmit={handleSubmit}>
                <div className='mb-3'>
                    <label htmlFor='correo_electronico'>Correo</label>
                    <input type='correo_electronico' placeholder='Enter correo_electronico' name='correo_electronico' onChange={handleInput} className='form-control rounded-0'></input>
                    {errors.correo_electronico && <span className='text-danger'>{errors.correo_electronico}</span>}
                </div>
                <div className='mb-3'>
                    <label htmlFor='contrasenna'>Contraseña</label>
                    <input type='contrasenna' placeholder='Enter contrasenna' name='contrasenna' onChange={handleInput} className='form-control rounded-0'></input>
                    {errors.contrasenna && <span className='text-danger'>{errors.contrasenna}</span>}
                </div>
                <button type='submit' className='btn btn-success w-100'>Iniciar Sesión</button>
                <p>Aceptar términos y condiciones</p>
                <Link to = '/signup' className='btn btn-default border w-100 text-decoration-none'>Crear cuenta</Link>
            </form>
        </div>   
    </div>
  )
}

export default Login
